import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Store } from '@ngrx/store';
import * as ProductsActions from './state/products/products.actions';
import { selectProductsList, selectProductsCount, selectProductsLoading } from './state/products/products.selectors';
import { ProductsListComponent } from './components/products-list.component';
import { MatCardModule } from '@angular/material/card';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { ReactiveFormsModule, FormBuilder } from '@angular/forms';

@Component({
  standalone: true,
  selector: 'app-products-page',
  imports: [CommonModule, ProductsListComponent, MatCardModule, MatFormFieldModule, MatInputModule, ReactiveFormsModule],
  template: `
    <mat-card>
      <h3>Products</h3>
      <form [formGroup]="form" (ngSubmit)="submit()" style="display:flex; gap:8px; align-items:end">
        <mat-form-field appearance="fill"><mat-label>Min Rating</mat-label><input matInput formControlName="min_rating" /></mat-form-field>
        <mat-form-field appearance="fill"><mat-label>Ordering</mat-label><input matInput formControlName="ordering" /></mat-form-field>
        <button mat-raised-button color="primary" type="submit">Filter</button>
      </form>

      <div *ngIf="loading$ | async">Chargement…</div>
      <div>Count: {{ count$ | async }}</div>
      <app-products-list [products]="products$ | async"></app-products-list>
    </mat-card>
  `,
})
export class ProductsPageComponent {
  form = this.fb.group({ min_rating: [0], ordering: ['-created_at'] });

  products$ = this.store.select(selectProductsList);
  count$ = this.store.select(selectProductsCount);
  loading$ = this.store.select(selectProductsLoading);

  constructor(private store: Store, private fb: FormBuilder) {}

  submit() {
    const q = this.form.value;
    this.store.dispatch(ProductsActions.loadProducts(q));
  }
}

